mapper.insert:
insert into login (id, loginaccount, loginpasswd
  )
values (#{id,jdbcType=INTEGER}, #{loginaccount,jdbcType=VARCHAR}, #{loginpasswd,jdbcType=VARCHAR}
  )

mapper.update:
update login
set loginaccount = #{loginaccount,jdbcType=VARCHAR},
  loginpasswd = #{loginpasswd,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, loginaccount, loginpasswd
from login
where id = #{id,jdbcType=INTEGER}
