mapper.insert:
insert into login (login_uuid, loginaccount, loginpasswd
  )
values (#{login_uuid,jdbcType=VARCHAR}, #{loginaccount,jdbcType=VARCHAR}, #{loginpasswd,jdbcType=VARCHAR}
  )

mapper.update:
update login
set loginaccount = #{loginaccount,jdbcType=VARCHAR},
  loginpasswd = #{loginpasswd,jdbcType=VARCHAR}
where login_uuid = #{login_uuid,jdbcType=VARCHAR}

mapper.select:
select login_uuid, loginaccount, loginpasswd
from login
where login_uuid = #{login_uuid,jdbcType=VARCHAR}
